mapper.select:
SELECT id, content, nickname, parent_id
FROM comment
WHERE id = #{id}
  AND delete_whether = false;

mapper.update:
UPDATE comment
SET group_id = #{id}
WHERE id = #{id}

mapper.select:
SELECT id, content, nickname, parent_id, group_id
FROM comment
WHERE post_id = #{postId}
  AND parent_id = #{parentId}
  AND delete_whether = false
ORDER BY id DESC

SELECT password
FROM comment
WHERE id = #{id}

mapper.update:
